sysauto_script.script:
var howLongToClose = new gs.getProperty('x_58872_needit.autoCloseOverdue');

var VeryOverdue = new GlideRecord('x_58872_needit_needit_task');

VeryOverdue.addQuery('due_date','<=',gs.daysAgo((howLongToClose)));
VeryOverdue.addQuery('state','<',3);

VeryOverdue.query();

while(VeryOverdue.next()){
	gs.info("Auto close of " + VeryOverdue.getRowCount() + " NeedIt Tasks.");
	VeryOverdue.work_notes = "This record is too old and has been closed";
	VeryOverdue.state = 4;
	VeryOverdue.update();
}
